import axios from "axios";
import {Navigate} from "react-router-dom";
import {useState} from "react";
// import GoogleLogin from "react-google-login";
import { GoogleLogin } from "@react-oauth/google";
// import {gapi} from "gapi-script";
import {useEffect} from "react";
// import 'bootstrap/dist/css/bootstrap.css';


export const Login = () => {
    const clientId = "799185628627-vmgk7pkl7qo6ca5gam2fro2qa6v3m06u.apps.googleusercontent.com";

      const onSuccess = async (res) => {
        console.log('succzddgsfgess:', res.accessToken);
        const user = {
          grant_type: "convert_token",
          client_id:
            "799185628627-vmgk7pkl7qo6ca5gam2fro2qa6v3m06u.apps.googleusercontent.com",
          client_secret: "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",
          backend: "google-oauth2",
          token: res.accessToken,
        };
            //console.log(user)
            const {data} = await axios.post('http://localhost:8000/api-auth/convert-token/', user ,{headers: {
                'Content-Type': 'application/json'
            }}, {withCredentials: true});

            //console.log(data, data['access_token'])
            axios.defaults.headers.common['Authorization'] = `Bearer ${data['access_token']}`;
            localStorage.clear();
            localStorage.setItem('access_token', data.access_token);
            localStorage.setItem('refresh_token', data.refresh_token);
            window.location.href = '/'
      }

      const onFailure = (err) => {
        console.log('failed:', err);
      };

    

    return (
      <div className="Auth-form-container">
        <GoogleLogin
          onSuccess={(credentialResponse) => {
            console.log(credentialResponse);
          }}
          onError={() => {
            console.log("Login Failed");
          }}
        />
        ;
      </div>
    );
}
