import axios from "axios";
import { Navigate } from "react-router-dom";
import { useState } from "react";
// import GoogleLogin from "react-google-login";
import { GoogleLogin } from "@react-oauth/google";
// import {gapi} from "gapi-script";
import { useEffect } from "react";
// import 'bootstrap/dist/css/bootstrap.css';

export const Login = () => {
  const clientId =
    "140549363583-00rh8vdh92h6lfd4motbkfns8nimqk0n.apps.googleusercontent.com";

  const onSuccess = async (res) => {
    console.log("user credentials:", res);
    
    const user = {
      grant_type: "convert_token",
      client_id: "JKJh74gcWHMwbBdCfwAZJa6nolIlw8r5JGith1CG",
      client_secret:
        "pbkdf2_sha256$600000$u0i3wcLGiP2IcOzlNJXymb$K0KiO5jjF0UZPIzfxit09eG8Bt/wHQZrmb2SQhTrBOk=",
      backend: "google-oauth2",
      token: res.credential,
    };
    
    console.log(user)

    try {
       const { data } = await axios.post(
         "http://localhost:8000/api-auth/convert-token/",
         user,
         {
           headers: {
             "Content-Type": "application/json",
           },
          //  withCredentials: true,
         },
       );
      console.log("Response:", data);
      axios.defaults.headers.common['Authorization'] = `Bearer ${data['access_token']}`;
      localStorage.clear();
      localStorage.setItem('access_token', data.access_token);
      localStorage.setItem('refresh_token', data.refresh_token);
      window.location.href = '/'
    } catch (error) {
      console.log("error: ", error);
    }
  };

  const onFailure = (err) => {
    console.log("failed:", err);
  };

  return (
    <div className="Auth-form-container">
      <GoogleLogin
        onSuccess={onSuccess}
        //   onSuccess={(credentialResponse) => {
        //     console.log(credentialResponse);
        //   }}
        //   onError={() => {
        //     console.log("Login Failed");
        //   }}
        onError={onFailure}
      />
    </div>
  );
};
