import axios from "axios";
import { Navigate } from "react-router-dom";
import { useState } from "react";
import { useGoogleLogin, googleLogout } from "@react-oauth/google";
import {jwtDecode} from "jwt-decode"

export const Login = () => {

  const login = useGoogleLogin({
    onSuccess: async (codeResponse) => {
      console.log(codeResponse)
      const user = {
        backend: "google-oauth2",
        client_id: "iBzvk3Uq6XssKDLrEhGMqYlsVKMmeHucErk1GjqF",
        client_secret:
          "vxW4H3xKXwNL6z3bXTheBEsQjgP9upCgglItvvPr5R3CWl8TaJZG1gl9Ae5RgqPbLQ51N8csSVgcWE88TETAqhpRXFwcvEBpAr6dvi34yOOfAnbQc8ha7aa6yOiYMjgn",
        grant_type: "convert_token",
        token: codeResponse.access_token,
      };

      try{
          const response = await fetch("http://127.0.0.1:8000/api-auth/convert-token", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            withCredential: true,
            body: JSON.stringify(user),
          });
          const data = await response.json();
          console.log("data: ", data);
          // if(data.access_token !== null) {
          //   Navigate("/home")
          // }
          if (response.status === 200) {
            console.log("loggedin");
            setAuthToken(data);
            setUser(jwtDecode(data.access));
            localStorage.setItem("authTokens", JSON.stringify(data));
            navigate("/");
          } else {
            console.log(response.status);
            console.log("there was a server issue");
            alert("something went wrong " + response.status);
          }
      } catch (error){
        console.log(error)
      }
      
    },
    flow: "implicit",

  });

  return (
    <div className="Auth-form-container">
      <button onClick={() => login()} auto_select>Google Login</button>
      <button onClick={() => googleLogout()}>LogOut</button>
    </div>
  );
};
