import axios from "axios";
import { Navigate } from "react-router-dom";
import { useState } from "react";
import { GoogleLogin } from "@react-oauth/google";
import {jwtDecode} from "jwt-decode"

export const Login = () => {
  // const clientId =
  //   "140549363583-00rh8vdh92h6lfd4motbkfns8nimqk0n.apps.googleusercontent.com";

  const onSuccess = async (res) => {
    console.log("user credentials:", res);

    const googleResponse = {
      clientId: res.clientId,
      userCredential: res.credential,
    }

    

    localStorage.setItem("credential", googleResponse.userCredential)
    const DecodedToken = jwtDecode(googleResponse.userCredential)
    console.log(DecodedToken)

    console.log(
      "google response: " )
      console.log(localStorage.getItem("credential"))
    
    const user = {
      backend: "google-oauth2",
      client_id: "iBzvk3Uq6XssKDLrEhGMqYlsVKMmeHucErk1GjqF",
      client_secret:
        "vxW4H3xKXwNL6z3bXTheBEsQjgP9upCgglItvvPr5R3CWl8TaJZG1gl9Ae5RgqPbLQ51N8csSVgcWE88TETAqhpRXFwcvEBpAr6dvi34yOOfAnbQc8ha7aa6yOiYMjgn",
      grant_type: "convert_token",
      token: res.credential,
    };
    
    console.log(user)

    try {
      const response = await fetch(
        "http://127.0.0.1:8000/api-auth/convert-token/",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/x-www-form-urlencoded",
          },
          withCredential: true,
          body: JSON.stringify(user),
        }
      );

      const data = await response.json();
      console.log(data);

      //  const { data } = await axios.post(
      //    "http://localhost:8000/api-auth/convert-token/",
      //    user,
      //    {
      //      headers: {
      //        "Content-Type": "application/json",
      //        "Access-Control-Allow-Credentials": "*",
      //      },
      //      withCredentials: true,
      //    },
      //  );
      console.log("Response:", data);
      // axios.defaults.headers.common['Authorization'] = `Bearer ${data['access_token']}`;
      // localStorage.clear();
      // localStorage.setItem('access_token', data.access_token);
      // localStorage.setItem('refresh_token', data.refresh_token);
      // window.location.href = '/'
    } catch (error) {
      console.log("error: ", error);
      console.log("error config: ", error.config)
    }
  };

  const onFailure = (err) => {
    console.log("failed:", err);
  };

  return (
    <div className="Auth-form-container">
      <GoogleLogin
        onSuccess={onSuccess}
        //   onSuccess={(credentialResponse) => {
        //     console.log(credentialResponse);
        //   }}
        //   onError={() => {
        //     console.log("Login Failed");
        //   }}
        onError={onFailure}
      />
    </div>
  );
};
