import { useGoogleLogin} from "@react-oauth/google";
import { useNavigate } from "react-router-dom";
import GoogleButton from "react-google-button";


export const Login = () => {
  const navigate = useNavigate()
  const login = useGoogleLogin({
    onSuccess: async (codeResponse) => {
      console.log("codeResponse: ", codeResponse)
      const user = {
        backend: "google-oauth2",
        client_id: "iBzvk3Uq6XssKDLrEhGMqYlsVKMmeHucErk1GjqF",
        client_secret:
          "vxW4H3xKXwNL6z3bXTheBEsQjgP9upCgglItvvPr5R3CWl8TaJZG1gl9Ae5RgqPbLQ51N8csSVgcWE88TETAqhpRXFwcvEBpAr6dvi34yOOfAnbQc8ha7aa6yOiYMjgn",
        grant_type: "convert_token",
        token: codeResponse.access_token,
      };

      fetch(
        `https://www.googleapis.com/oauth2/v3/userinfo?access_token=${codeResponse.access_token}`,
        {
          headers: {
            Authorization: `Bearer ${codeResponse.access_token}`,
            Accept: "application/json",
          },
        }
      )
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! Status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          console.log("User information:", data);
          // Handle the user information as needed
        })
        .catch((error) => {
          console.error("Error fetching user information:", error);
          // Handle errors gracefully
        });

      try{
          const response = await fetch("http://127.0.0.1:8000/api-auth/convert-token", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            withCredential: true,
            body: JSON.stringify(user),
          });
          const data = await response.json();
          console.log("data: ", data);
          if (response.status === 200) {
            console.log("loggedin");
            localStorage.setItem("access_token", data.access_token)
            navigate("/dashboard");
          } else {
            console.log(response.status);
            console.log("there was a server issue");
            alert("something went wrong " + response.status);
          }
      } catch (error){
        console.log(error)
      }
      
    },
    flow: "implicit",

  });

  return (
    <div className="Auth-form-container">
      <button onClick={() => login()} auto_select = {true}>Google Login</button>
    </div>
  );
};
