import React, {useState, useContext} from 'react'
import {
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
} from 'mdb-react-ui-kit';
import AuthContext from "../context/AuthContext";


function ResetPassword() {
  const [basicModal, setBasicModal] = useState(false);

  const toggleOpen = () => setBasicModal(!basicModal);

  return (
    <>
      <MDBBtn onClick={toggleOpen}>LAUNCH DEMO MODAL</MDBBtn>
      <MDBModal open={basicModal} setopen={setBasicModal} tabIndex='-1'>
        <MDBModalDialog size='lg' centered>
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Modal title</MDBModalTitle>
              <MDBBtn className='btn-close' color='none' onClick={toggleOpen}></MDBBtn>
            </MDBModalHeader>
              <MDBModalBody>
                <EmailForm />
              </MDBModalBody>
            <MDBModalFooter>
              <MDBBtn color='secondary' onClick={toggleOpen}>
                Close
              </MDBBtn>
              <MDBBtn>Submit</MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  );
}











function EmailForm() {
    
    const [email, setEmail] = useState("");
    const [content, setContent] = useState("")

    const {resetPassword} = useContext(AuthContext)


    const handleResetPassword = async (e) => {
        e.preventDefault()
        if(resetPassword(email)){
            setContent("An email containe has been sent to your registered email id ")
        }

    }

  return (
    <div>
        <h1>Reset Password</h1>
        <form onSubmit={handleResetPassword}>
            <label>Enter the registered email id: </label>
            <input id='email' type='email' name = "email" onChange={(e) => setEmail(e.target.value)}></input>
            <button type='submit'>Submit</button>
        </form>
    </div>
  )
}

export {ResetPassword}
